$(document).ready(function () {
    // Document ready on top of screen
    $(this).scrollTop(0);

    // initialize WOW js
    new WOW().init();

    // Cache selectors
    var lastId,
        topMenu = $(".navbar-nav"),
        topMenuHeight = topMenu.outerHeight() + 162,
        // All list items
        menuItems = topMenu.find("a"),
        // Anchors corresponding to menu items
        scrollItems = menuItems.map(function () {
            var item = $($(this).attr("href"));
            if (item.length) { return item; }
        });


    // Bind to scroll
    $(window).scroll(function () {
        // Get container scroll position
        var fromTop = $(this).scrollTop() + topMenuHeight;

        // Get id of current scroll item
        var cur = scrollItems.map(function () {
            if ($(this).offset().top < fromTop)
                return this;
        });
        // Get the id of the current element
        cur = cur[cur.length - 1];
        var id = cur && cur.length ? cur[0].id : "";

        if (lastId !== id) {
            lastId = id;
            // Set/remove active class
            
            menuItems.parent().removeClass("active").end().filter("[href='#" + id + "']").parent().addClass("active");
        }
        if ($(window).width() < 769) {
            var windscroll = $(window).scrollTop();
            $('#content section').each(function (i) {
                if ($(this).position().top <= windscroll + 2) {
                    $('nav li.active').removeClass('active');
                    $('nav li').eq(i).addClass('active');
                }
            });
        }

        if ($(window).scrollTop() >= 48) {
            $('nav').addClass('box-shadow');

        } else {
            $('nav').removeClass('box-shadow');
        }
    });

    menuItems.click(function (e) {
        var href = $(this).attr("href"),
            offsetTopMobile = href === "#" ? 0 : $(href).offset().top - 50,
            offsetTop = href === "#" ? 0 : $(href).offset().top - topMenuHeight + 40;
        

        if ($(window).width() < 950) {
            $('html, body').stop().animate({
                scrollTop: offsetTopMobile
            }, 800);
            $("#slideMenu").toggleClass("show");
        } else {
            $('html, body').stop().animate({
                scrollTop: offsetTop
            }, 800);
        }

        e.preventDefault();
    });

    $("#chevronDown").click(function() {
        $('html, body').animate({
            scrollTop: $("#latarBelakang").offset().top - topMenuHeight + 40
        }, 800);
    });
});